drop function IF EXISTS INSTANCE_MREF_F
GO

create function INSTANCE_MREF_F  (
 aINSTANCE_ref varchar(255)
 ,aLang varchar(25)
) returns varchar(255)  
DETERMINISTIC
 begin  
 declare aMREF varchar(255);
  set aMREF='to do';
return aMREF;
end 
GO


