  , '' , ifnull(name,'') ,' '  )
  into abrief   from objectmode  where  objectmodeid = aobjectmodeid; 
 select defaultmode
 into aec  from objectmode where objectmodeid=aobjectmodeid;
  case aec 
when -1 then 
  select concat(abrief ,
 'да; ') into abrief ;
when 0 then 
  select concat(abrief ,
 'нет; ') into abrief ;
 else 
  select concat(abrief ,
 '; ') into abrief ;
  end case; 
else
  set abrief= '';
end if;
return abrief;
end  
