 declare abrief varchar(255);
 declare atmpstr varchar(255);
 declare atmpbrief varchar(255);
 declare atmpid binary(16);
 declare atmpmr varchar(255); 
 declare amlftemp varchar(255);
 declare amlfbrief varchar(255);
 declare aec int;
if atod_stid is null then  set abrief=''; return abrief; end if;
select count(*) into aec from tod_st where tod_stid=atod_stid;
if aec<>0 then
  set abrief='';
  select concat(abrief 
  , '' , ifnull(invn,'') ,' '  )
  into abrief   from tod_st  where  tod_stid = atod_stid; 
  select concat(abrief 
  , '' , ifnull(name,'') ,' '  )
  into abrief   from tod_st  where  tod_stid = atod_stid; 
else
  set abrief= '';
end if;
return abrief;
end  
