  set abrief='';
 select accesible
 into aec  from roles_act where roles_actid=aroles_actid;
  case aec 
when 1 then 
  select concat(abrief ,
 'да; ') into abrief ;
when 0 then 
  select concat(abrief ,
 'нет; ') into abrief ;
 else 
  select concat(abrief ,
 '; ') into abrief ;
  end case; 
  select concat(abrief 
  , '' , ifnull(menuname,'') ,' '  )
  into abrief   from roles_act  where  roles_actid = aroles_actid; 
  select concat(abrief 
  , '' , ifnull(menucode,'') ,' '  )
  into abrief   from roles_act  where  roles_actid = aroles_actid; 
else
  set abrief= '';
end if;
return abrief;
end  
