    leave body;
 end if;
 insert into genmanualcode 
 ( genmanualcodeid 
,parentstructrowid
,name

,the_alias

,code

 ) select 
copymap(acopyaction,b2g(genmanualcodeid)) 
,copymap(acopyaction,b2g(parentstructrowid))
,name

,the_alias

,code

 from genmanualcode
 where parentstructrowid =g2b(aparentstructrowid); 
 end 
go
GO


