 start transaction;  
 insert into   nextstate
 (  changestamp,nextstateid 
,parentstructrowid
,thestate

 ) values (now(), g2b(anextstateid) 
,g2b(aparentstructrowid)
,g2b(athestate)

 ) ;


call nextstate_client_trigger(acursession,anextstateid);

 end if;
 commit; 
select 'ok' result;
 end 
go
