,g2b(aextenderobject)

,ashablonbrief

,arulebrief

,aisjormalchange

,ausearchiving

,aintegerpkey

,aparticoncls

 ) ;
-- if asessuserlogin<>'replicator'  then
 if aparentrowid is null then
   select count(*) into  auniquerowcount from part where  parentstructrowid=g2b(aparentstructrowid) and parentrowid is null 
 and sequence=asequence;
