  where  fieldsrcdefid = g2b(afieldsrcdefid) ;


call fieldsrcdef_client_trigger(acursession,afieldsrcdefid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'mtzmetamodel.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=fieldsrcdef' result;
    leave body;
  end if;
select count(*) into aec from fieldsrcdef where 
parentstructrowid=g2b(aparentstructrowid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <fieldsrcdef>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   fieldsrcdef
 (  changestamp,fieldsrcdefid 
,parentstructrowid
,provider

,connectionstring

,datasource

,idfield

,briefstring

,filterstring

,sortfield

,descriptionstring

,dontshowdialog

 ) values (now(), g2b(afieldsrcdefid) 
,g2b(aparentstructrowid)
,aprovider

,aconnectionstring

,adatasource

,aidfield

,abriefstring

,afilterstring

,asortfield

,adescriptionstring

,adontshowdialog

 ) ;


call fieldsrcdef_client_trigger(acursession,afieldsrcdefid);

 end if;
 commit; 
select 'ok' result;
 end 
go
