    leave body;
 end if;
 insert into partmenu 
 ( partmenuid 
,parentstructrowid
,name

,caption

,tooltip

,the_action

,ismenuitem

,istoolbarbutton

,hotkey

 ) select 
copymap(acopyaction,b2g(partmenuid)) 
,copymap(acopyaction,b2g(parentstructrowid))
,name

,caption

,tooltip

,the_action

