 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update fieldsrcdef set lockuserid =null  where fieldsrcdefid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update fieldsrcdef set locksessionid =null  where fieldsrcdefid=arowid;
    leave body;
   end if;
 end 
go
GO


