drop procedure if exists tod_valtype_brief
go

create procedure tod_valtype_brief  (
 acursession varchar(38),
 atod_valtypeid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if atod_valtypeid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from tod_valtype where tod_valtypeid=g2b(atod_valtypeid);
 if  aec >0 then
   set abrief=tod_valtype_brief_f(g2b(atod_valtypeid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists tod_valtype_delete
go

create procedure tod_valtype_delete /**/ (
 acursession varchar(38),
 atod_valtypeid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from tod_valtype where tod_valtypeid=g2b(atod_valtypeid);
if aec>0 then
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=tod_valtype' result;
    leave body;
  end if;
 call tod_valtype_logger(acursession,atod_valtypeid) ; 
  delete from  tod_valtype 
  where  tod_valtypeid = g2b(atod_valtypeid) ;
  delete from num_values where ownerpartname='tod_valtype' and ownerrowid=g2b(atod_valtypeid);
  end if;
    select 'ok' result;
 end 
go
GO

/*тип измерения*/

drop procedure if exists tod_valtype_save
go

create procedure tod_valtype_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 atod_valtypeid varchar(38)
,aname
 varchar (255)/* название *//* название */
,aedizm varchar(38)/* ед. изм. *//* ед. изм. */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from tod_valtype where tod_valtypeid=g2b(atod_valtypeid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=tod_valtype' result;
    leave body;
  end if;
 start transaction ; 
 call tod_valtype_logger(acursession,atod_valtypeid) ; 
 update  tod_valtype set changestamp=now() 
,
  name=aname
,
  edizm=g2b(aedizm)
  where  tod_valtypeid = g2b(atod_valtypeid) ;


call tod_valtype_client_trigger(acursession,atod_valtypeid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=tod_valtype' result;
    leave body;
  end if;
 start transaction;  
 insert into   tod_valtype
 (  changestamp,tod_valtypeid 
,instanceid
,name

,edizm

 ) values (now(), g2b(atod_valtypeid) 
,g2b(ainstanceid)
,aname

,g2b(aedizm)

 ) ;


call tod_valtype_client_trigger(acursession,atod_valtypeid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists tod_valtype_copy
go

create procedure tod_valtype_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into tod_valtype 
 ( tod_valtypeid 
,instanceid
,name

,edizm

 ) select 
copymap(acopyaction,b2g(tod_valtypeid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,edizm

 from tod_valtype
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


