drop procedure if exists tod_system_parent
go
create procedure tod_system_parent /* */ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from tod_system where  tod_systemid=arowid;
 end 
go
GO


drop procedure if exists tod_system_islocked
go
create procedure tod_system_islocked /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from tod_system where tod_systemid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists tod_system_lock
go
create procedure tod_system_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call tod_system_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update tod_system set lockuserid =auserid ,locksessionid=null  where tod_systemid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update tod_system set lockuserid=null ,locksessionid =acursession  where tod_systemid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists tod_system_unlock
go
create procedure tod_system_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call tod_system_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update tod_system set lockuserid =null  where tod_systemid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update tod_system set locksessionid =null  where tod_systemid=arowid;
    leave body;
   end if;
 end 
go
GO


drop procedure IF EXISTS tod_system_client_trigger
GO

create procedure tod_system_client_trigger  (
 acursession varchar(38),
 atod_systemid varchar(38)
) 
 begin  
   declare aBRIEF varchar(255);
  set aBRIEF='to do';
end 
GO


drop procedure if exists tod_st_brief
go

create procedure tod_st_brief  (
 acursession varchar(38),
 atod_stid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if atod_stid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from tod_st where tod_stid=g2b(atod_stid);
 if  aec >0 then
   set abrief=tod_st_brief_f(g2b(atod_stid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists tod_st_delete
go

create procedure tod_st_delete /**/ (
 acursession varchar(38),
 atod_stid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from tod_st where tod_stid=g2b(atod_stid);
if aec>0 then
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=tod_st' result;
    leave body;
  end if;
 call tod_st_logger(acursession,atod_stid) ; 
  delete from  tod_st 
  where  tod_stid = g2b(atod_stid) ;
  delete from num_values where ownerpartname='tod_st' and ownerrowid=g2b(atod_stid);
  end if;
    select 'ok' result;
 end 
go
GO

/*станки*/

drop procedure if exists tod_st_save
go

create procedure tod_st_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 atod_stid varchar(38)
,ainvn
 varchar (10)/* инвентарный номер *//* инвентарный номер */
,aname
 varchar (255)/* название *//* название */
,athe_model varchar(38)/* модель станка *//* модель станка */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from tod_st where tod_stid=g2b(atod_stid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=tod_st' result;
    leave body;
  end if;
 start transaction ; 
 call tod_st_logger(acursession,atod_stid) ; 
 update  tod_st set changestamp=now() 
,
  invn=ainvn
,
  name=aname
,
  the_model=g2b(athe_model)
  where  tod_stid = g2b(atod_stid) ;


call tod_st_client_trigger(acursession,atod_stid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'tod.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=tod_st' result;
    leave body;
  end if;
 start transaction;  
 insert into   tod_st
 (  changestamp,tod_stid 
,instanceid
,invn

,name

,the_model

 ) values (now(), g2b(atod_stid) 
,g2b(ainstanceid)
,ainvn

,aname

,g2b(athe_model)

 ) ;


call tod_st_client_trigger(acursession,atod_stid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


