,theversion

,theplatform

,thecomment

 ) select 
copymap(acopyaction,b2g(workplaceid)) 
,copymap(acopyaction,b2g(instanceid))
,name

,caption

,theversion

,theplatform

,thecomment

 from workplace
 where instanceid =g2b( ainstanceid); 
 end 
go
