go

create procedure to_schedinfo_brief  (
 acursession varchar(38),
 ato_schedinfoid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if ato_schedinfoid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from to_schedinfo where to_schedinfoid=g2b(ato_schedinfoid);
 if  aec >0 then
   set abrief=to_schedinfo_brief_f(g2b(ato_schedinfoid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists to_schedinfo_delete
go

create procedure to_schedinfo_delete /**/ (
 acursession varchar(38),
 ato_schedinfoid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_schedinfo where to_schedinfoid=g2b(ato_schedinfoid);
if aec>0 then
 select checkoperation( acursession ,'tosched.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=to_schedinfo' result;
    leave body;
  end if;
 call to_schedinfo_logger(acursession,ato_schedinfoid) ; 
  delete from  to_schedinfo 
  where  to_schedinfoid = g2b(ato_schedinfoid) ;
  delete from num_values where ownerpartname='to_schedinfo' and ownerrowid=g2b(ato_schedinfoid);
  end if;
    select 'ok' result;
 end 
go
GO

/*описание*/

drop procedure if exists to_schedinfo_save
go

create procedure to_schedinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 ato_schedinfoid varchar(38)
,adfrom
 date/* с *//* с */
,adto
 date/* по *//* по */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_schedinfo where to_schedinfoid=g2b(ato_schedinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'tosched.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=to_schedinfo' result;
    leave body;
  end if;
 start transaction ; 
 call to_schedinfo_logger(acursession,ato_schedinfoid) ; 
 update  to_schedinfo set changestamp=now() 
,
  dfrom=adfrom
,
  dto=adto
  where  to_schedinfoid = g2b(ato_schedinfoid) ;


call to_schedinfo_client_trigger(acursession,ato_schedinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'tosched.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=to_schedinfo' result;
    leave body;
  end if;
select count(*) into aec from to_schedinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <to_schedinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   to_schedinfo
 (  changestamp,to_schedinfoid 
,instanceid
,dfrom

,dto

 ) values (now(), g2b(ato_schedinfoid) 
,g2b(ainstanceid)
,adfrom

,adto

 ) ;


call to_schedinfo_client_trigger(acursession,ato_schedinfoid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists to_schedinfo_copy
go

create procedure to_schedinfo_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into to_schedinfo 
 ( to_schedinfoid 
,instanceid
,dfrom

,dto

 ) select 
copymap(acopyaction,b2g(to_schedinfoid)) 
,copymap(acopyaction,b2g(instanceid))
,dfrom

,dto

 from to_schedinfo
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


drop procedure if exists to_schedinfo_logger
go

create procedure to_schedinfo_logger /**/ (
 acursession varchar(38),
 ato_schedinfoid varchar(38)
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
    leave body;
 end if;
 end 
go
GO


drop procedure if exists to_schedinfo_parent
go
create procedure to_schedinfo_parent /* */ (
 acursession varchar(38),
 arowid binary(16) ,
out  aparentid binary(16),
out aparenttable varchar(255)
) body:begin  
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
  set aparenttable = 'instance';
  select instanceid into aparentid from to_schedinfo where  to_schedinfoid=arowid;
 end 
go
GO


drop procedure if exists to_schedinfo_islocked
go
create procedure to_schedinfo_islocked /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 out aislocked integer
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare alockuserid binary(16);
 declare alocksessionid binary(16);
 declare aparenttable varchar(255); 
declare aec  int;
 set aislocked = 0;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select  usersid into auserid  from the_session where the_sessionid=acursession;
 select  lockuserid,locksessionid into alockuserid, alocksessionid from to_schedinfo where to_schedinfoid=arowid;
 /* verify this row */
 if not alockuserid is null  then
   if  alockuserid <> auserid  then
     set aislocked = 4; /* checkout by another user */
     leave body;
   else 
     set aislocked = 2; /* checkout by caller */
     leave body;
   end if;  
 end if;  
 if not alocksessionid is null then 
   if  alocksessionid <> acursession  then
     set aislocked = 3 ;/* lockes by another user */
     leave body;
   else 
     set aislocked = 1; /* locked by caller */
     leave body;
   end if;  
 end if;  
 set aislocked = 0; 
 end 
 go
GO


drop procedure if exists to_schedinfo_lock
go
create procedure to_schedinfo_lock /*  */ (
 acursession varchar(38),
 arowid binary(16) ,
 alockmode integer 
) body:begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare atmpid binary(16);
 declare aaccess integer;
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call to_schedinfo_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update to_schedinfo set lockuserid =auserid ,locksessionid=null  where to_schedinfoid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update to_schedinfo set lockuserid=null ,locksessionid =acursession  where to_schedinfoid=arowid;
     leave body;
   end if;
 end 
 go 
GO


drop procedure if exists to_schedinfo_unlock
go
create procedure to_schedinfo_unlock /*  */ (
 acursession varchar(38),
 arowid binary(16) 
) body: begin  
 declare aparentid binary(16);
 declare auserid binary(16);
 declare aislocked integer;
 declare aparenttable varchar(255); 
declare aec  int;
select count(*) into aec from the_session where the_sessionid=acursession and closed=0 ;
if aec=0  then
    leave body;
end if;
 select usersid into auserid  from the_session where the_sessionid=acursession;
 call to_schedinfo_islocked( acursession,arowid,aislocked );
 if aislocked >=3  then
    leave body;
   end if;
   if  aislocked =2  then
    update to_schedinfo set lockuserid =null  where to_schedinfoid=arowid;
    leave body;
   end if;
   if  aislocked =1  then
    update to_schedinfo set locksessionid =null  where to_schedinfoid=arowid;
    leave body;
   end if;
 end 
go
GO


