 select checkoperation( acursession ,'totask.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=to_taskvalues' result;
    leave body;
  end if;
 start transaction;  
 insert into   to_taskvalues
 (  changestamp,to_taskvaluesid 
,instanceid
,checkvalue

,thevalue

,thecomment

 ) values (now(), g2b(ato_taskvaluesid) 
,g2b(ainstanceid)
,g2b(acheckvalue)

,athevalue

,athecomment

 ) ;


call to_taskvalues_client_trigger(acursession,ato_taskvaluesid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists to_taskvalues_copy
go

