 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into to_taskvalues 
 ( to_taskvaluesid 
,instanceid
,checkvalue

,thevalue

,thecomment

 ) select 
copymap(acopyaction,b2g(to_taskvaluesid)) 
,copymap(acopyaction,b2g(instanceid))
,checkvalue

,thevalue

,thecomment

 from to_taskvalues
 where instanceid =g2b( ainstanceid); 
 end 
go
