drop procedure if exists to_taskinfo_brief
go

create procedure to_taskinfo_brief  (
 acursession varchar(38),
 ato_taskinfoid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if ato_taskinfoid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from to_taskinfo where to_taskinfoid=g2b(ato_taskinfoid);
 if  aec >0 then
   set abrief=to_taskinfo_brief_f(g2b(ato_taskinfoid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists to_taskinfo_delete
go

create procedure to_taskinfo_delete /**/ (
 acursession varchar(38),
 ato_taskinfoid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_taskinfo where to_taskinfoid=g2b(ato_taskinfoid);
if aec>0 then
 select checkoperation( acursession ,'totask.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=to_taskinfo' result;
    leave body;
  end if;
 call to_taskinfo_logger(acursession,ato_taskinfoid) ; 
  delete from  to_taskinfo 
  where  to_taskinfoid = g2b(ato_taskinfoid) ;
  delete from num_values where ownerpartname='to_taskinfo' and ownerrowid=g2b(ato_taskinfoid);
  end if;
    select 'ok' result;
 end 
go
GO

