drop procedure if exists to_taskinfo_save
go

create procedure to_taskinfo_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 ato_taskinfoid varchar(38)
,athemachine varchar(38)/* станок *//* станок */
,aoper varchar(38)/* оператор *//* оператор */
,athecard varchar(38)/* диагностическая карта *//* диагностическая карта */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_taskinfo where to_taskinfoid=g2b(ato_taskinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'totask.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=to_taskinfo' result;
    leave body;
  end if;
 start transaction ; 
 call to_taskinfo_logger(acursession,ato_taskinfoid) ; 
 update  to_taskinfo set changestamp=now() 
,
  themachine=g2b(athemachine)
,
  oper=g2b(aoper)
,
  thecard=g2b(athecard)
  where  to_taskinfoid = g2b(ato_taskinfoid) ;


call to_taskinfo_client_trigger(acursession,ato_taskinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'totask.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=to_taskinfo' result;
    leave body;
  end if;
select count(*) into aec from to_taskinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <to_taskinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   to_taskinfo
 (  changestamp,to_taskinfoid 
,instanceid
,themachine

,oper

,thecard

 ) values (now(), g2b(ato_taskinfoid) 
,g2b(ainstanceid)
,g2b(athemachine)

,g2b(aoper)

,g2b(athecard)

 ) ;


call to_taskinfo_client_trigger(acursession,ato_taskinfoid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


