drop procedure if exists to_oper_brief
go

create procedure to_oper_brief  (
 acursession varchar(38),
 ato_operid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if ato_operid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from to_oper where to_operid=g2b(ato_operid);
 if  aec >0 then
   set abrief=to_oper_brief_f(g2b(ato_operid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists to_oper_delete
go

create procedure to_oper_delete /**/ (
 acursession varchar(38),
 ato_operid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_oper where to_operid=g2b(ato_operid);
if aec>0 then
 select checkoperation( acursession ,'toop.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=to_oper' result;
    leave body;
  end if;
 call to_oper_logger(acursession,ato_operid) ; 
  delete from  to_oper 
  where  to_operid = g2b(ato_operid) ;
  delete from num_values where ownerpartname='to_oper' and ownerrowid=g2b(ato_operid);
  end if;
    select 'ok' result;
 end 
go
GO

/*оператор*/

drop procedure if exists to_oper_save
go

create procedure to_oper_save /**/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 ato_operid varchar(38)
,aname
 varchar (255)/* имя *//* имя */
,afamilyname
 varchar (256)/* фамилия *//* фамилия */
,asurname
 varchar (255)/* отчество *//* отчество */
,atnum
 varchar (20)/* табельный номер *//* табельный номер */
,alogin
 varchar (64)/* логин *//* логин */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_oper where to_operid=g2b(ato_operid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'toop.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=to_oper' result;
    leave body;
  end if;
 start transaction ; 
 call to_oper_logger(acursession,ato_operid) ; 
 update  to_oper set changestamp=now() 
,
  name=aname
,
  familyname=afamilyname
,
  surname=asurname
,
  tnum=atnum
,
  login=alogin
  where  to_operid = g2b(ato_operid) ;


call to_oper_client_trigger(acursession,ato_operid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'toop.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=to_oper' result;
    leave body;
  end if;
select count(*) into aec from to_oper where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <to_oper>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   to_oper
 (  changestamp,to_operid 
,instanceid
,name

,familyname

,surname

,tnum

,login

 ) values (now(), g2b(ato_operid) 
,g2b(ainstanceid)
,aname

,afamilyname

,asurname

,atnum

,alogin

 ) ;


call to_oper_client_trigger(acursession,ato_operid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


