drop procedure if exists to_cardinfo_brief
go

create procedure to_cardinfo_brief  (
 acursession varchar(38),
 ato_cardinfoid varchar(38),
out abrief varchar(255)
) body: begin  
 declare aaccess int;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aec int;
 declare alang2 varchar(25);
 select count(*) into aec  from the_session where the_sessionid=g2b(acursession) and closed=0 ;
 if aec=0 then
     set abrief=''; leave body;
  end if;
 select lang into alang2 from the_session where the_sessionid=g2b(acursession);
 if ato_cardinfoid is null then set abrief=''; leave body; end if;
 select  count(*) into aec from to_cardinfo where to_cardinfoid=g2b(ato_cardinfoid);
 if  aec >0 then
   set abrief=to_cardinfo_brief_f(g2b(ato_cardinfoid), alang2);
 else
   set abrief= 'неверный идентификатор';
 end if;
 set abrief=left(abrief,255);
end 
go
GO


drop procedure if exists to_cardinfo_delete
go

create procedure to_cardinfo_delete /*описание диагностической карты*/ (
 acursession varchar(38),
 ato_cardinfoid varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare asyslogid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 declare atmpid binary(16);
 declare aec int;
 select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0 then
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_cardinfo where to_cardinfoid=g2b(ato_cardinfoid);
if aec>0 then
 select checkoperation( acursession ,'tocard.edit') into aaccess;
 if aaccess=0  then
    select 'удаление строк не разрешено. раздел=to_cardinfo' result;
    leave body;
  end if;
 call to_cardinfo_logger(acursession,ato_cardinfoid) ; 
  delete from  to_cardinfo 
  where  to_cardinfoid = g2b(ato_cardinfoid) ;
  delete from num_values where ownerpartname='to_cardinfo' and ownerrowid=g2b(ato_cardinfoid);
  end if;
    select 'ok' result;
 end 
go
GO

/*описание*/

drop procedure if exists to_cardinfo_save
go

create procedure to_cardinfo_save /*описание диагностической карты*/ (
 acursession varchar(38),
ainstanceid varchar(38) ,
 ato_cardinfoid varchar(38)
,athe_machine varchar(38)/* станок *//* станок */
,acard_date
 date/* дата составления карты *//* дата составления карты */
,acard_archived
 integer/* архивная карта *//* архивная карта */
)  body: begin  
declare asyslogid binary(16);
 declare auniquerowcount integer;
 declare atmpstr varchar(255);
 declare atmpid binary(16);
 declare aaccess int;
 declare asysinstid binary(16);
 -- declare asessuserid binary(16);
 declare amlf_partid binary(16);
 -- declare asessuserlogin varchar(40);
 declare aec int;
-- select usersid into asessuserid from the_session where the_sessionid=g2b(acursession);
-- select login into asessuserlogin from users where usersid=asessuserid;
-- select instanceid into asysinstid from instance where objtype='mtzsystem';
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
  select 'сессия уже завершена.' result;
    leave body;
  end if;
/*if exists */ select  count(*) into aec from to_cardinfo where to_cardinfoid=g2b(ato_cardinfoid);
if aec >0 then
 --  update  --
 --  verify access  --
 select checkoperation( acursession ,'tocard.edit') into aaccess;
 if aaccess=0  then
    select 'изменение строк не разрешено. раздел=to_cardinfo' result;
    leave body;
  end if;
 start transaction ; 
 call to_cardinfo_logger(acursession,ato_cardinfoid) ; 
 update  to_cardinfo set changestamp=now() 
,
  the_machine=g2b(athe_machine)
,
  card_date=acard_date
,
  card_archived=acard_archived
  where  to_cardinfoid = g2b(ato_cardinfoid) ;


call to_cardinfo_client_trigger(acursession,ato_cardinfoid);

 else
 --  insert  --
 --  verify access  --
 select checkoperation( acursession ,'tocard.edit') into aaccess;
 if aaccess=0  then
    select 'добавление строк не разрешено. раздел=to_cardinfo' result;
    leave body;
  end if;
select count(*) into aec from to_cardinfo where 
instanceid=g2b(ainstanceid);
if aec >0 then 
    select 'невозможно создать вторую строку в однострочной сессии. раздел: <to_cardinfo>' result;
    rollback;
    leave body;
 end if;
 start transaction;  
 insert into   to_cardinfo
 (  changestamp,to_cardinfoid 
,instanceid
,the_machine

,card_date

,card_archived

 ) values (now(), g2b(ato_cardinfoid) 
,g2b(ainstanceid)
,g2b(athe_machine)

,acard_date

,acard_archived

 ) ;


call to_cardinfo_client_trigger(acursession,ato_cardinfoid);

 end if;
 commit; 
select 'ok' result;
 end 
go
GO


drop procedure if exists to_cardinfo_copy
go

create procedure to_cardinfo_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into to_cardinfo 
 ( to_cardinfoid 
,instanceid
,the_machine

,card_date

,card_archived

 ) select 
copymap(acopyaction,b2g(to_cardinfoid)) 
,copymap(acopyaction,b2g(instanceid))
,the_machine

,card_date

,card_archived

 from to_cardinfo
 where instanceid =g2b( ainstanceid); 
 end 
go
GO


