    leave body;
end if;
 select auserid = usersid  from the_session where the_sessionid=acursession;
 call to_cardinfo_islocked( acursession,arowid,aislocked);
 if aislocked >=3  then
    leave body;
  end if;
   if  alockmode =2  then
    update to_cardinfo set lockuserid =auserid ,locksessionid=null  where to_cardinfoid=arowid;
     leave body;
   end if;
   if  alockmode =1  then
    update to_cardinfo set lockuserid=null ,locksessionid =acursession  where to_cardinfoid=arowid;
     leave body;
   end if;
 end 
 go 
GO


