,mat

 ) select 
copymap(acopyaction,b2g(to_carddevicesid)) 
,copymap(acopyaction,b2g(parentstructrowid))
,mat

 from to_carddevices
 where parentstructrowid =g2b(aparentstructrowid); 
 end 
go
GO


drop procedure if exists to_cardchecks_copy
go

create procedure to_cardchecks_copy (
 acursession varchar(38),
 acopyaction varchar(38),
 ainstanceid varchar(38) 
)  body: begin  
 declare aec int;
declare aid binary(16);
declare fetch_done int default false;
declare copy_cursor_to_cardchecks cursor for
select to_cardchecksid from to_cardchecks where 
instanceid =ainstanceid;
 declare continue handler for not found set fetch_done = true;
 select count(*) into aec from the_session where the_sessionid=g2b(acursession) and closed=0 ;
if aec=0  then
    leave body;
 end if;
 insert into to_cardchecks 
 ( to_cardchecksid 
,instanceid
,the_system

,the_check

,normochas

,valuetype

,lowvalue

,hivalue

,the_comment

,tagid

,the_image

,the_doc

 ) select 
copymap(acopyaction,b2g(to_cardchecksid)) 
,copymap(acopyaction,b2g(instanceid))
,the_system

,the_check

,normochas

,valuetype

,lowvalue

,hivalue

,the_comment

,tagid

,the_image

,the_doc

 from to_cardchecks
 where instanceid =g2b( ainstanceid); 
open copy_cursor_to_cardchecks;
  set  fetch_done=false;
fetch copy_cursor_to_cardchecks into aid;
while not fetch_done do 
    call to_carddevices_copy(acursession,acopyaction,ainstanceid,aid);
  set  fetch_done=false;
    fetch copy_cursor_to_cardchecks into aid;
end while;

close copy_cursor_to_cardchecks;
 end 
go
GO


