if aec=0 then
insert into typelist(typelistid, name,registerproc,deleteproc, hclproc, propagateproc) values(g2b(uuid()),'mtz2job', 'mtz2job_register', 'mtz2job_delete', 'mtz2job_hcl', 'mtz2job_propagate');
