/* SessionCheckPoint  Автоматическое закрытие "Подвисших" сессий*/
create or replace procedure SessionCheckPoint

(aCURSESSION CHAR/* Текущая сессия */

)
 as 
cursor s is select the_sessionid id from the_session
where lastAccess <sysdate()-1;
s_rec s%rowtype;
begin
--open s;
for s_rec in s loop
  logout(s_rec.id);
End loop;
Close s;
end;





