if ato_taskvaluesid is null  then  aBRIEF:=' '; return (aBRIEF); end if;
 -- Brief body -- 
select count(*) into aTmpCnt from to_taskvalues where to_taskvaluesID=ato_taskvaluesID;
if aTmpCnt >0 then
  aBRIEF:='';
select checkvalue
 into atmpID  from to_taskvalues  where  to_taskvaluesID = ato_taskvaluesID ;
 atmpBrief := func.to_taskchecks_BRIEF_F(atmpID);
  aBRIEF:= aBRIEF || '{' || nvl(atmpbrief,' ') || '}; ';
 else 
  aBRIEF:= '-';
end if;
 aBRIEF:=substr(aBRIEF,1,255);
return (aBRIEF);
end;


