 Kernel.INSTANCE_OWNER (acursession,row_PARAMETERS.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_PARAMETERS.id);
end loop;
--close chld_PARAMETERS;
end ;
  delete from  PARAMETERS 
  where  PARAMETERSID = aPARAMETERSID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

