,aName

,athe_Alias

,aFromPart

,aField

,aAggregation

,aExpression

,aForCombo

 ); 
 ViewColumn_SINIT( aCURSESSION,aViewColumnid,atmpid);
 -- checking unique constraints  --


 end if;
