then
 raise_application_error(-20000,'Нарущение уникальности сочетания полей. Раздел=FIELD');
 return;
end if;
select Count(*) into aUniqueRowCount from FIELD where ParentStructRowID=aParentStructRowID 
 and Sequence=aSequence;
