,Code

 ) values ( aFIELDEXPRESSIONID 
,aParentStructRowID
,aTarget

,aCode

 ); 
 FIELDEXPRESSION_SINIT( aCURSESSION,aFIELDEXPRESSIONid,atmpid);
 -- checking unique constraints  --


 end if;
