 update FIELDPARAMMAP set securitystyleid =aStyleID where FIELDPARAMMAPid = aRowID;
else 
 update FIELDPARAMMAP set securitystyleid =aSecurityStyleID where FIELDPARAMMAPid = aRowID;
end if; 
end ; 

