procedure tod_st_BRIEF  (
 aCURSESSION CHAR,
 atod_stid CHAR,
 aBRIEF out varchar2
) as 
 aaccess integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 existsCnt integer;
begin
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
if atod_stid is null then aBRIEF:=''; return; end if;
 -- Brief body -- 
select count(*)into existsCnt from tod_st where tod_stID=atod_stID;
if existsCnt >0
 then
 --  verify access  --
 select  SecurityStyleID into atmpid from tod_st where tod_stid=atod_stID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'BRIEF',aaccess=>aaccess); 
 if aaccess=0 
  then
    raise_application_error(-20000,'No access for BRIEF Structure=tod_st');
    return;
  end if;
  aBRIEF:=func.tod_st_BRIEF_F(atod_stid);
else
  aBRIEF:= 'неверный идентификатор';
end if;
 aBRIEF:=substr(aBRIEF,1,255);
end; 

procedure tod_st_DELETE /**/ (
 aCURSESSION CHAR,
 atod_stid CHAR,
 ainstanceid char
) as 
 aSysLogID CHAR(38);
 aaccess integer;
 aSysInstID CHAR(38);
 atmpID CHAR(38);
 existsCnt integer;
 aChildListid CHAR(38);
  begin  
 select  Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0 then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
end if;
 -- Delete body -- 
select count(*) into existsCnt from tod_st where tod_stID=atod_stID;
if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from tod_st where tod_stid=atod_stID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW',aaccess=>aaccess ) ;
 if aaccess=0 then 
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW:tod_st',aaccess=>aaccess); 
    if aaccess=0 then
      raise_application_error(-20000,'Нет прав на удаление. Раздел=tod_st');
      return;
    end if;
  end if;
 --  verify lock  --
 tod_st_ISLOCKED( acursession=>acursession,aROWID=>atod_stid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=tod_st');
    return;
  end if;
  --begin tran--  
 -- erase child items --
select  newid() into aSysLogid from SYS.DUAL;
 MTZSystem.SysLog_SAVE (aCURSESSION=>acursession ,aTheSession=>acursession, aInstanceID=>aSysInstID, aSysLogid=>aSysLogid, aLogStructID => 'tod_st',
 aVERB=>'DELETEROW',  aThe_Resource=>atod_stid, aLogInstanceID=>aInstanceID);
declare cursor chld_tod_st is select  instanceid ID from instance where OwnerPartName ='tod_st' and OwnerRowID=atod_stid;
row_tod_st  chld_tod_st%ROWTYPE;
begin
--open chld_tod_st;
for row_tod_st in chld_tod_st loop
 Kernel.INSTANCE_OWNER (acursession,row_tod_st.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_tod_st.id);
end loop;
--close chld_tod_st;
end ;
  delete from  tod_st 
  where  tod_stID = atod_stID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

