procedure tod_st_SAVE /**/ (
 aCURSESSION CHAR,
 atod_stid CHAR,
aInstanceID CHAR 
,aINVN
 VARCHAR2/* Инвентарный номер *//* Инвентарный номер */
,aName
 VARCHAR2/* Название *//* Название */
,athe_model CHAR := null /* Модель станка *//* Модель станка */
) as 
aSysLogid CHAR(38);
 aUniqueRowCount integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 aaccess int;
 aSysInstID CHAR(38);
 existsCnt integer;
 begin  
 select Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt =0 
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 -- Insert / Update body -- 
select count(*) into existsCnt from tod_st where tod_stID=atod_stID;
if existsCnt >0
 then
 --  UPDATE  --
 --  verify access  --
 select SecurityStyleID into atmpID from tod_st where tod_stid=atod_stID;
 CheckVerbRight( acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW',aaccess=>aaccess); 
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW:tod_st',aaccess=>aaccess ); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на модификацию. Раздел=tod_st');
      return;
    end if;
  end if;
 --  verify lock  --
 tod_st_ISLOCKED( acursession=>acursession,aROWID=>atod_stid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=tod_st');
    return;
  end if;
 -- update row  --
select newid() into asyslogid from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'tod_st',
 aVERB=>'EDITROW',  aThe_Resource=>atod_stid,aLogInstanceID=>aInstanceID);
 update  tod_st set ChangeStamp=sysdate
,
  INVN=aINVN
,
  Name=aName
,
  the_model=athe_model
  where  tod_stID = atod_stID; 
 -- checking unique constraints  --


 else
 --  INSERT  --
 --  verify access  --
 select  SecurityStyleID into atmpID from instance where instanceid=ainstanceid;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW',aaccess=>aaccess );
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW:tod_st',aaccess=>aaccess); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на создание строк. Раздел=tod_st');
      return;
    end if;
 end if;
 LATIR4.Kernel.instance_ISLOCKED( acursession=>acursession,aROWID=>aInstanceID,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=tod_st');
    return;
  end if;
select newid() into aSysLogID from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'tod_st',
 aVERB=>'CREATEROW',  aThe_Resource=>atod_stid,aLogInstanceID=>aInstanceID);
 insert into   tod_st
 (  tod_stID 
,InstanceID
,INVN

,Name

,the_model

 ) values ( atod_stID 
,aInstanceID
,aINVN

,aName

,athe_model

 ); 
 tod_st_SINIT( aCURSESSION,atod_stid,atmpid);
 -- checking unique constraints  --


 end if;
 -- close transaction --
 end; 

