if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from Shortcut where Shortcutid=aShortcutID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW',aaccess=>aaccess ) ;
 if aaccess=0 then 
