      raise_application_error(-20000,'Нет прав на удаление. Раздел=Shortcut');
      return;
    end if;
  end if;
 --  verify lock  --
 Shortcut_ISLOCKED( acursession=>acursession,aROWID=>aShortcutid,aIsLocked=>aaccess); 
 if aaccess>2 
