 insert into   SysLog
 (  SysLogID 
,InstanceID
,TheSession

,the_Resource

,LogStructID

,VERB

,LogInstanceID

 ) values ( aSysLogID 
,aInstanceID
,aTheSession

,athe_Resource

,aLogStructID

,aVERB

,aLogInstanceID

 ); 
 SysLog_SINIT( aCURSESSION,aSysLogid,atmpid);
 -- checking unique constraints  --


 end if;
