if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
  select ParentStructRowID into aParentID  from ARMJRNLREP where  ARMJRNLREPid=aRowID;
  aParentTable := 'ARMJournal';
 end; 

