, aParentStructRowID CHAR 
,aName
 VARCHAR2/* Название *//* Название */
,aTheExtention CHAR/* Расширение *//* Расширение */
) as 
 aUniqueRowCount integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 aaccess int;
 aSysInstID CHAR(38);
 existsCnt integer;
 begin  
