,aAllObjects
 NUMBER/* Вся фирма *//* Вся фирма */
,aColegsObject
 NUMBER/* Объекты коллег *//* Объекты коллег */
,aSubStructObjects
 NUMBER/* Подчиненные подразделения *//* Подчиненные подразделения */
) as 
aSysLogid CHAR(38);
 aUniqueRowCount integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
