 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW:ROLES2_MODULE',aaccess=>aaccess); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на создание строк. Раздел=ROLES2_MODULE');
      return;
    end if;
 end if;
 LATIR4.Kernel.instance_ISLOCKED( acursession=>acursession,aROWID=>aInstanceID,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=ROLES2_MODULE');
    return;
  end if;
select newid() into aSysLogID from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'ROLES2_MODULE',
 aVERB=>'CREATEROW',  aThe_Resource=>aROLES2_MODULEid,aLogInstanceID=>aInstanceID);
 insert into   ROLES2_MODULE
 (  ROLES2_MODULEID 
,InstanceID
,GroupName

,Caption

,Sequence

,ModuleAccessible

,CustomizeVisibility

,TheIcon

,name

,TheComment

