 -- Delete body -- 
select count(*) into existsCnt from ROLES_DOC_STATE where ROLES_DOC_STATEID=aROLES_DOC_STATEID;
if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from ROLES_DOC_STATE where ROLES_DOC_STATEid=aROLES_DOC_STATEID;
