procedure to_schedinfo_DELETE /**/ (
 aCURSESSION CHAR,
 ato_schedinfoid CHAR,
 ainstanceid char
) as 
 aSysLogID CHAR(38);
 aaccess integer;
 aSysInstID CHAR(38);
 atmpID CHAR(38);
 existsCnt integer;
 aChildListid CHAR(38);
  begin  
 select  Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0 then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
end if;
 -- Delete body -- 
select count(*) into existsCnt from to_schedinfo where to_schedinfoID=ato_schedinfoID;
if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from to_schedinfo where to_schedinfoid=ato_schedinfoID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW',aaccess=>aaccess ) ;
 if aaccess=0 then 
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW:to_schedinfo',aaccess=>aaccess); 
    if aaccess=0 then
      raise_application_error(-20000,'Нет прав на удаление. Раздел=to_schedinfo');
      return;
    end if;
  end if;
 --  verify lock  --
 to_schedinfo_ISLOCKED( acursession=>acursession,aROWID=>ato_schedinfoid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=to_schedinfo');
    return;
  end if;
  --begin tran--  
 -- erase child items --
select  newid() into aSysLogid from SYS.DUAL;
 MTZSystem.SysLog_SAVE (aCURSESSION=>acursession ,aTheSession=>acursession, aInstanceID=>aSysInstID, aSysLogid=>aSysLogid, aLogStructID => 'to_schedinfo',
 aVERB=>'DELETEROW',  aThe_Resource=>ato_schedinfoid, aLogInstanceID=>aInstanceID);
declare cursor chld_to_schedinfo is select  instanceid ID from instance where OwnerPartName ='to_schedinfo' and OwnerRowID=ato_schedinfoid;
row_to_schedinfo  chld_to_schedinfo%ROWTYPE;
begin
--open chld_to_schedinfo;
