     return;
   end if; 
 end if; 
 to_taskcomment_HCL (acursession,ROW_to_taskcomment.id,aisLocked);
 if aisLocked >2 then
   close lch_to_taskcomment;
   return;
 end if;
 end loop;
--close lch_to_taskcomment;
end;
