select securityStyleid into aSSID from to_taskcomment where to_taskcommentid=aRowid;
end;


procedure to_taskvalues_BRIEF  (
 aCURSESSION CHAR,
 ato_taskvaluesid CHAR,
 aBRIEF out varchar2
) as 
 aaccess integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 existsCnt integer;
begin
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
if ato_taskvaluesid is null then aBRIEF:=''; return; end if;
 -- Brief body -- 
select count(*)into existsCnt from to_taskvalues where to_taskvaluesID=ato_taskvaluesID;
if existsCnt >0
 then
 --  verify access  --
 select  SecurityStyleID into atmpid from to_taskvalues where to_taskvaluesid=ato_taskvaluesID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'BRIEF',aaccess=>aaccess); 
 if aaccess=0 
  then
    raise_application_error(-20000,'No access for BRIEF Structure=to_taskvalues');
    return;
  end if;
  aBRIEF:=func.to_taskvalues_BRIEF_F(ato_taskvaluesid);
else
  aBRIEF:= 'неверный идентификатор';
end if;
 aBRIEF:=substr(aBRIEF,1,255);
end; 

procedure to_taskvalues_DELETE /**/ (
 aCURSESSION CHAR,
 ato_taskvaluesid CHAR,
 ainstanceid char
) as 
 aSysLogID CHAR(38);
 aaccess integer;
 aSysInstID CHAR(38);
 atmpID CHAR(38);
 existsCnt integer;
 aChildListid CHAR(38);
  begin  
 select  Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0 then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
end if;
 -- Delete body -- 
select count(*) into existsCnt from to_taskvalues where to_taskvaluesID=ato_taskvaluesID;
if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from to_taskvalues where to_taskvaluesid=ato_taskvaluesID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW',aaccess=>aaccess ) ;
 if aaccess=0 then 
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW:to_taskvalues',aaccess=>aaccess); 
    if aaccess=0 then
      raise_application_error(-20000,'Нет прав на удаление. Раздел=to_taskvalues');
      return;
    end if;
  end if;
 --  verify lock  --
 to_taskvalues_ISLOCKED( acursession=>acursession,aROWID=>ato_taskvaluesid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=to_taskvalues');
    return;
  end if;
  --begin tran--  
 -- erase child items --
select  newid() into aSysLogid from SYS.DUAL;
 MTZSystem.SysLog_SAVE (aCURSESSION=>acursession ,aTheSession=>acursession, aInstanceID=>aSysInstID, aSysLogid=>aSysLogid, aLogStructID => 'to_taskvalues',
 aVERB=>'DELETEROW',  aThe_Resource=>ato_taskvaluesid, aLogInstanceID=>aInstanceID);
declare cursor chld_to_taskvalues is select  instanceid ID from instance where OwnerPartName ='to_taskvalues' and OwnerRowID=ato_taskvaluesid;
row_to_taskvalues  chld_to_taskvalues%ROWTYPE;
begin
--open chld_to_taskvalues;
for row_to_taskvalues in chld_to_taskvalues loop
 Kernel.INSTANCE_OWNER (acursession,row_to_taskvalues.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_to_taskvalues.id);
end loop;
--close chld_to_taskvalues;
end ;
  delete from  to_taskvalues 
  where  to_taskvaluesID = ato_taskvaluesID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

