 begin  
 -- checking the_session  --
select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if  existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 select usersid into auserID from the_session where the_sessionid=acursession;
 to_taskvalues_ISLOCKED( aCURSESSION,aROWID,aISLocked);
 if aIsLocked >=3  
  then
    raise_application_error(-20000,'Строка заблоирована другим пользователем');
    return;
  end if;
   if  aIsLocked =2  
   then   
    update to_taskvalues set LockUserID =null  where to_taskvaluesid=aRowID;
     return;
   end if;
   if  aIsLocked =1  
   then   
    update to_taskvalues set LockSessionID =null  where to_taskvaluesid=aRowID;
     return;
   end if;
 end; 

procedure to_taskvalues_SINIT /**/ (
 aCURSESSION CHAR,
 aRowID CHAR ,
 aSecurityStyleID CHAR
) as 
 aParentID CHAR(38);
 aParentTable varchar2(255); 
 aStr varchar2(4000);
 aStyleID CHAR(38);
 atmpID CHAR(38);
 aaccess integer; 
begin
 select  SecurityStyleID into atmpID from to_taskvalues where to_taskvaluesid=aROWID;
  CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'SECURE',aaccess=>aaccess); 
 if aaccess=0 
  then
    raise_application_error(-20000,'Нет прав на управление защитой. Раздел =to_taskvalues');
    return;
  end if;
if aSecurityStyleID is null then
 to_taskvalues_parent( aCURSESSION,aROWID,aParentID ,aParentTable);
  astr:= 'select SecurityStyleID  from ' || aParentTable || ' where ' ||aParentTable || 'id=:1' ;
  execute immediate astr into aStyleID using aParentid;
 update to_taskvalues set securitystyleid =aStyleID where to_taskvaluesid = aRowID;
else 
 update to_taskvalues set securitystyleid =aSecurityStyleID where to_taskvaluesid = aRowID;
end if; 
end ; 

procedure to_taskvalues_propagate /**/ (
 aCURSESSION CHAR,
 aRowID CHAR
) as 
achildlistid CHAR(38);
aSSID CHAR(38);
begin
select securityStyleid into aSSID from to_taskvalues where to_taskvaluesid=aRowid;
end;


procedure to_taskinfo_BRIEF  (
 aCURSESSION CHAR,
 ato_taskinfoid CHAR,
 aBRIEF out varchar2
) as 
 aaccess integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 existsCnt integer;
begin
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
if ato_taskinfoid is null then aBRIEF:=''; return; end if;
 -- Brief body -- 
select count(*)into existsCnt from to_taskinfo where to_taskinfoID=ato_taskinfoID;
if existsCnt >0
 then
 --  verify access  --
 select  SecurityStyleID into atmpid from to_taskinfo where to_taskinfoid=ato_taskinfoID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'BRIEF',aaccess=>aaccess); 
 if aaccess=0 
  then
    raise_application_error(-20000,'No access for BRIEF Structure=to_taskinfo');
    return;
  end if;
  aBRIEF:=func.to_taskinfo_BRIEF_F(ato_taskinfoid);
else
  aBRIEF:= 'неверный идентификатор';
end if;
 aBRIEF:=substr(aBRIEF,1,255);
end; 

procedure to_taskinfo_DELETE /**/ (
 aCURSESSION CHAR,
 ato_taskinfoid CHAR,
 ainstanceid char
) as 
 aSysLogID CHAR(38);
 aaccess integer;
 aSysInstID CHAR(38);
 atmpID CHAR(38);
 existsCnt integer;
 aChildListid CHAR(38);
  begin  
 select  Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0 then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
end if;
 -- Delete body -- 
select count(*) into existsCnt from to_taskinfo where to_taskinfoID=ato_taskinfoID;
if existsCnt >0 then
 --  verify access  --
 select   SecurityStyleID into atmpID from to_taskinfo where to_taskinfoid=ato_taskinfoID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW',aaccess=>aaccess ) ;
 if aaccess=0 then 
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'DELETEROW:to_taskinfo',aaccess=>aaccess); 
    if aaccess=0 then
      raise_application_error(-20000,'Нет прав на удаление. Раздел=to_taskinfo');
      return;
    end if;
  end if;
 --  verify lock  --
 to_taskinfo_ISLOCKED( acursession=>acursession,aROWID=>ato_taskinfoid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=to_taskinfo');
    return;
  end if;
  --begin tran--  
 -- erase child items --
select  newid() into aSysLogid from SYS.DUAL;
 MTZSystem.SysLog_SAVE (aCURSESSION=>acursession ,aTheSession=>acursession, aInstanceID=>aSysInstID, aSysLogid=>aSysLogid, aLogStructID => 'to_taskinfo',
 aVERB=>'DELETEROW',  aThe_Resource=>ato_taskinfoid, aLogInstanceID=>aInstanceID);
declare cursor chld_to_taskinfo is select  instanceid ID from instance where OwnerPartName ='to_taskinfo' and OwnerRowID=ato_taskinfoid;
row_to_taskinfo  chld_to_taskinfo%ROWTYPE;
begin
--open chld_to_taskinfo;
for row_to_taskinfo in chld_to_taskinfo loop
 Kernel.INSTANCE_OWNER (acursession,row_to_taskinfo.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_to_taskinfo.id);
end loop;
--close chld_to_taskinfo;
end ;
  delete from  to_taskinfo 
  where  to_taskinfoID = ato_taskinfoID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

/*Описание*/
procedure to_taskinfo_SAVE /**/ (
 aCURSESSION CHAR,
 ato_taskinfoid CHAR,
aInstanceID CHAR 
,atheMachine CHAR/* Станок *//* Станок */
,aoper CHAR/* Оператор *//* Оператор */
,atheCard CHAR := null /* Диагностическая карта *//* Диагностическая карта */
) as 
aSysLogid CHAR(38);
 aUniqueRowCount integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 aaccess int;
 aSysInstID CHAR(38);
 existsCnt integer;
 begin  
 select Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt =0 
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 -- Insert / Update body -- 
select count(*) into existsCnt from to_taskinfo where to_taskinfoID=ato_taskinfoID;
if existsCnt >0
 then
 --  UPDATE  --
 --  verify access  --
 select SecurityStyleID into atmpID from to_taskinfo where to_taskinfoid=ato_taskinfoID;
 CheckVerbRight( acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW',aaccess=>aaccess); 
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW:to_taskinfo',aaccess=>aaccess ); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на модификацию. Раздел=to_taskinfo');
      return;
    end if;
  end if;
 --  verify lock  --
 to_taskinfo_ISLOCKED( acursession=>acursession,aROWID=>ato_taskinfoid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=to_taskinfo');
    return;
  end if;
 -- update row  --
select newid() into asyslogid from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'to_taskinfo',
 aVERB=>'EDITROW',  aThe_Resource=>ato_taskinfoid,aLogInstanceID=>aInstanceID);
 update  to_taskinfo set ChangeStamp=sysdate
,
  theMachine=atheMachine
,
  oper=aoper
,
  theCard=atheCard
  where  to_taskinfoID = ato_taskinfoID; 
 -- checking unique constraints  --


 else
 --  INSERT  --
 --  verify access  --
 select  SecurityStyleID into atmpID from instance where instanceid=ainstanceid;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW',aaccess=>aaccess );
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW:to_taskinfo',aaccess=>aaccess); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на создание строк. Раздел=to_taskinfo');
      return;
    end if;
 end if;
 LATIR4.Kernel.instance_ISLOCKED( acursession=>acursession,aROWID=>aInstanceID,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=to_taskinfo');
    return;
  end if;
select Count(*) into existsCnt from to_taskinfo where 
InstanceID=aInstanceID;
if existsCnt >0 
 then
    raise_application_error(-20000,'Невозможно создать вторую строку в однострочной сессии. Раздел: <to_taskinfo>');
    return;
 End if;
select newid() into aSysLogID from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'to_taskinfo',
 aVERB=>'CREATEROW',  aThe_Resource=>ato_taskinfoid,aLogInstanceID=>aInstanceID);
 insert into   to_taskinfo
 (  to_taskinfoID 
,InstanceID
,theMachine

,oper

,theCard

 ) values ( ato_taskinfoID 
,aInstanceID
,atheMachine

,aoper

,atheCard

 ); 
 to_taskinfo_SINIT( aCURSESSION,ato_taskinfoid,atmpid);
 -- checking unique constraints  --


 end if;
 -- close transaction --
 end; 

procedure to_taskinfo_PARENT /**/ (
 aCURSESSION CHAR,
 aRowID CHAR ,
 aParentID out CHAR ,
 aParentTable out varchar2
) as 
existsCnt integer;
 begin  
 -- checking the_session  --
select count(*)into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
  aParentTable := 'INSTANCE';
  select  INSTANCEID into aParentID from to_taskinfo where  to_taskinfoid=aRowID;
 end; 

procedure to_taskinfo_ISLOCKED /**/ (
 aCURSESSION CHAR,
 aRowID CHAR ,
 aIsLocked out integer
) as 
 aParentID CHAR(38);
 aUserID CHAR(38);
 aLockUserID CHAR(38);
 aLockSessionID CHAR(38);
 aParentTable varchar2(255); 
 existsCnt integer; 
  astr varchar2(4000);
begin
 aisLocked := 0;
 -- checking the_session  --
select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0;
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 select usersid into auserID from the_session where the_sessionid=acursession;
 select  LockUserID,LockSessionID into aLockUserID, aLockSessionID from to_taskinfo where to_taskinfoid=aRowID;
 /* verify this row */
 if not aLockUserID is null  
 then   
   if  aLockUserID <> auserID  
   then   
     aisLocked := 4; /* CheckOut by another user */
     return;
   else 
     aisLocked := 2; /* CheckOut by caller */
     return;
   end  if; 
 end if;  
 if not aLockSessionID is null  
 then   
   if  aLockSessionID <> aCURSESSION  
   then   
     aisLocked := 3; /* Lockes by another user */
     return;
   else 
     aisLocked := 1; /* Locked by caller */
     return;
   end if;  
 end if;  
 aisLocked := 0; 
  to_taskinfo_parent (aCURSESSION,aROWID,aParentID,aParentTable);
  if aparenttable='INSTANCE' then
      astr := 'begin Kernel.' || aPARENTTABLE || '_islocked (:1,:2,:3); end;';
    Else
      astr := 'begin totask.' || aPARENTTABLE || '_islocked (:1,:2,:3); end;';
    end if;
  execute immediate  astr using aCURSESSION,aParentID ,out aISLocked;
 end; 

procedure to_taskinfo_LOCK /**/ (
 aCURSESSION CHAR,
 aRowID CHAR ,
 aLockMode integer 
) as 
 aParentID CHAR(38);
 aUserID CHAR(38);
 atmpID CHAR(38);
 aaccess integer;
 aIsLocked integer;
 aParentTable varchar2(255); 
 existsCnt integer; 
 begin  
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0;
 -- checking the_session  --
if existsCnt=0
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 select usersid into auserid from  the_session where the_sessionid=acursession;
 to_taskinfo_ISLOCKED (aCURSESSION,aROWID,aISLocked );
 if aIsLocked >=3  
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем');
    return;
  end if;
 if aIsLocked =0  
 then
  to_taskinfo_HCL (acursession,aRowID,aisLocked);
  if aIsLocked >=3  
  then
     raise_application_error(-20000,'У данной строки имеются дочерние строки, которые заблокированы другим пользователем');
     return;
   end if;
 end if;
 select  SecurityStyleID into atmpID from to_taskinfo where to_taskinfoid=aROWID;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'LOCKROW',aaccess=>aaccess); 
 if aaccess=0 
 then
    raise_application_error(-20000,'Нет прав на блокировку строк. Раздел=to_taskinfo');
    return;
  end if;
   if  aLockMode =2  
   then   
    update to_taskinfo set LockUserID =auserID ,LockSessionID =null where to_taskinfoid=aRowID;
     return;
   end if;
   if  aLockMode =1  
   then   
    update to_taskinfo set LockUserID =null,LockSessionID =aCURSESSION  where to_taskinfoid=aRowID;
     return;
   end if;
 end ;

procedure to_taskinfo_HCL /**/ (
 aCURSESSION CHAR,
 aRowID CHAR ,
 aIsLocked out integer
) as 
achildlistid CHAR(38);
 aUserID CHAR(38);
 aLockUserID CHAR(38);
 aLockSessionID CHAR(38);
 begin  
 select usersid into auserID from the_session where the_sessionid=acursession;
aIsLocked :=0;
end;

