 LATIR4.Kernel.instance_ISLOCKED( acursession=>acursession,aROWID=>aInstanceID,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=to_taskchecks');
    return;
  end if;
select newid() into aSysLogID from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'to_taskchecks',
 aVERB=>'CREATEROW',  aThe_Resource=>ato_taskchecksid,aLogInstanceID=>aInstanceID);
