begin
--open chld_Users;
for row_Users in chld_Users loop
 Kernel.INSTANCE_OWNER (acursession,row_Users.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_Users.id);
end loop;
--close chld_Users;
end ;
  delete from  Users 
  where  UsersID = aUsersID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

