   if  aLockMode =2  
   then   
    update to_oper set LockUserID =auserID ,LockSessionID =null where to_operid=aRowID;
     return;
   end if;
   if  aLockMode =1  
   then   
    update to_oper set LockUserID =null,LockSessionID =aCURSESSION  where to_operid=aRowID;
     return;
   end if;
 end ;

