 Num_zones_ISLOCKED( acursession=>acursession,aROWID=>aNum_zonesid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=Num_zones');
    return;
  end if;
  --begin tran--  
