 Kernel.INSTANCE_DELETE (acursession,row_Num_zones.id);
end loop;
--close chld_Num_zones;
end ;
  delete from  Num_zones 
  where  Num_zonesID = aNum_zonesID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

