    end if;
  end if;
 --  verify lock  --
 to_cardinfo_ISLOCKED( acursession=>acursession,aROWID=>ato_cardinfoid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пользователем. Раздел=to_cardinfo');
    return;
  end if;
  --begin tran--  
 -- erase child items --
select  newid() into aSysLogid from SYS.DUAL;
 MTZSystem.SysLog_SAVE (aCURSESSION=>acursession ,aTheSession=>acursession, aInstanceID=>aSysInstID, aSysLogid=>aSysLogid, aLogStructID => 'to_cardinfo',
 aVERB=>'DELETEROW',  aThe_Resource=>ato_cardinfoid, aLogInstanceID=>aInstanceID);
declare cursor chld_to_cardinfo is select  instanceid ID from instance where OwnerPartName ='to_cardinfo' and OwnerRowID=ato_cardinfoid;
row_to_cardinfo  chld_to_cardinfo%ROWTYPE;
begin
--open chld_to_cardinfo;
for row_to_cardinfo in chld_to_cardinfo loop
 Kernel.INSTANCE_OWNER (acursession,row_to_cardinfo.id,null,null);
 Kernel.INSTANCE_DELETE (acursession,row_to_cardinfo.id);
end loop;
--close chld_to_cardinfo;
end ;
  delete from  to_cardinfo 
  where  to_cardinfoID = ato_cardinfoID; 
 end if;
 -- close transaction --
 <<del_error>>
 existsCnt:=0;
end;

/*Описание*/
procedure to_cardinfo_SAVE /*Описание диагностической карты*/ (
 aCURSESSION CHAR,
 ato_cardinfoid CHAR,
aInstanceID CHAR 
,athe_machine CHAR/* Станок *//* Станок */
,acard_date
 DATE := null /* Дата составления карты *//* Дата составления карты */
,acard_archived
 NUMBER := null /* Архивная карта *//* Архивная карта */
) as 
aSysLogid CHAR(38);
 aUniqueRowCount integer;
 atmpStr varchar2(255);
 atmpID CHAR(38);
 aaccess int;
 aSysInstID CHAR(38);
 existsCnt integer;
 begin  
 select Instanceid into aSysInstID from instance where objtype='MTZSYSTEM';
 -- checking the_session  --
 select count(*) into existsCnt from the_session where the_sessionid=acursession and closed=0 ;
if existsCnt =0 
  then
    raise_application_error(-20000,'Сессия уже завершена.');
    return;
  end if;
 -- Insert / Update body -- 
select count(*) into existsCnt from to_cardinfo where to_cardinfoID=ato_cardinfoID;
if existsCnt >0
 then
 --  UPDATE  --
 --  verify access  --
 select SecurityStyleID into atmpID from to_cardinfo where to_cardinfoid=ato_cardinfoID;
 CheckVerbRight( acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW',aaccess=>aaccess); 
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'EDITROW:to_cardinfo',aaccess=>aaccess ); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на модификацию. Раздел=to_cardinfo');
      return;
    end if;
  end if;
 --  verify lock  --
 to_cardinfo_ISLOCKED( acursession=>acursession,aROWID=>ato_cardinfoid,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=to_cardinfo');
    return;
  end if;
 -- update row  --
select newid() into asyslogid from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'to_cardinfo',
 aVERB=>'EDITROW',  aThe_Resource=>ato_cardinfoid,aLogInstanceID=>aInstanceID);
 update  to_cardinfo set ChangeStamp=sysdate
,
  the_machine=athe_machine
,
  card_date=acard_date
,
  card_archived=acard_archived
  where  to_cardinfoID = ato_cardinfoID; 
 -- checking unique constraints  --


 else
 --  INSERT  --
 --  verify access  --
 select  SecurityStyleID into atmpID from instance where instanceid=ainstanceid;
 CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW',aaccess=>aaccess );
 if aaccess=0 
  then
    CheckVerbRight (acursession=>acursession,aThe_Resource=>atmpID,averb=>'CREATEROW:to_cardinfo',aaccess=>aaccess); 
    if aaccess=0 
    then
      raise_application_error(-20000,'Нет прав на создание строк. Раздел=to_cardinfo');
      return;
    end if;
 end if;
 LATIR4.Kernel.instance_ISLOCKED( acursession=>acursession,aROWID=>aInstanceID,aIsLocked=>aaccess); 
 if aaccess>2 
  then
    raise_application_error(-20000,'Строка заблокирована другим пльзователем. Раздел=to_cardinfo');
    return;
  end if;
select Count(*) into existsCnt from to_cardinfo where 
InstanceID=aInstanceID;
if existsCnt >0 
 then
    raise_application_error(-20000,'Невозможно создать вторую строку в однострочной сессии. Раздел: <to_cardinfo>');
    return;
 End if;
select newid() into aSysLogID from sys.dual;
 MTZSystem.SysLog_SAVE( aTheSession=>acursession,aCURSESSION=>acursession, aInstanceID=>asysinstid, aSysLogid=>aSysLogid, aLogStructID => 'to_cardinfo',
 aVERB=>'CREATEROW',  aThe_Resource=>ato_cardinfoid,aLogInstanceID=>aInstanceID);
 insert into   to_cardinfo
 (  to_cardinfoID 
,InstanceID
,the_machine

,card_date

,card_archived

 ) values ( ato_cardinfoID 
,aInstanceID
,athe_machine

,acard_date

,acard_archived

 ); 
 to_cardinfo_SINIT( aCURSESSION,ato_cardinfoid,atmpid);
 -- checking unique constraints  --


 end if;
 -- close transaction --
 end; 

